CREATE OR REPLACE FUNCTION verifica_existe_conta_rubrica ( cd_conta_contabil_p text) RETURNS varchar AS $body$
DECLARE


cd_empresa_w		empresa.cd_empresa%type;
qt_existe_w		bigint;
ie_existe_w		varchar(1)	:= 'N';


BEGIN

begin
select	cd_empresa
into STRICT	cd_empresa_w
from	conta_contabil
where	cd_conta_contabil	= cd_conta_contabil_p;

select	count(*)
into STRICT	qt_existe_w
from	ctb_modelo_rubrica b,
	ctb_modelo_relat a
where	a.nr_sequencia	= b.nr_seq_modelo
and	a.cd_empresa	= cd_empresa_w
and	(b.ds_origem IS NOT NULL AND b.ds_origem::text <> '')
and	upper(b.ds_origem) like upper('%' || cd_conta_contabil_p || '%');

exception when others then
	cd_empresa_w		:= null;
	qt_existe_w		:= 0;
end;

if (qt_existe_w > 0) then
	ie_existe_w	:= 'S';
end if;

return ie_existe_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
