CREATE OR REPLACE FUNCTION verifica_existe_medidas ( cd_procedimento_p bigint, ie_origem_proced_p bigint, nr_seq_proc_interno_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);
cd_especialidade_w		bigint;
cd_grupo_proc_w		bigint;
cd_area_procedimento_w	bigint;
qt_registros_w		smallint;


BEGIN

select 	max(cd_especialidade),
	 max(cd_grupo_proc),
	 max(cd_area_procedimento)
into STRICT 	cd_especialidade_w,
	 cd_grupo_proc_w,
	 cd_area_procedimento_w
from 	estrutura_procedimento_v
where 	cd_procedimento = cd_procedimento_p
and 	ie_origem_proced = ie_origem_proced_p;

SELECT	COUNT(1) as qt_registros
into STRICT    	qt_registros_w
FROM    	medida_exame_laudo c,
        	tipo_medida_laudo b,
        	tipo_medida_laudo_proc a
WHERE   	a.nr_seq_tipo = b.nr_sequencia
AND     	b.nr_sequencia = c.nr_seq_tipo_medida
AND     	c.ie_situacao = 'A'
AND     	coalesce(b.ie_situacao,'A') = 'A'
AND     	((coalesce(a.nr_seq_proc_interno::text, '') = ''
        	AND  ((a.cd_procedimento = cd_procedimento_p AND CASE WHEN a.ie_origem_proced=0 THEN  ie_origem_proced_p  ELSE a.ie_origem_proced END  = ie_origem_proced_p)
        	OR   a.cd_especialidade = cd_especialidade_w
        	OR   a.cd_grupo_proc = cd_grupo_proc_w
        	OR   a.cd_area_procedimento = cd_area_procedimento_w))
        	OR (a.nr_seq_proc_interno = nr_seq_proc_interno_p));

if (qt_registros_w > 0) then
		ds_retorno_w :=	'S';
else
		ds_retorno_w :=	'N';
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
