CREATE OR REPLACE FUNCTION verifica_ficha_lote_ent (nr_seq_lote_sec_p bigint) RETURNS varchar AS $body$
DECLARE


qt_fichas_w		bigint;
qt_fichas_lote_w	bigint;
ds_retorno_w		varchar(1);

BEGIN
if (nr_seq_lote_sec_p IS NOT NULL AND nr_seq_lote_sec_p::text <> '') then
	select	count(*)
	into STRICT	qt_fichas_w
	from	lote_ent_sec_ficha
	where	nr_seq_lote_sec = nr_seq_lote_sec_p;

	select	coalesce(qt_fichas_lote,0)
	into STRICT	qt_fichas_lote_w
	from	lote_ent_secretaria
	where	nr_sequencia = nr_seq_lote_sec_p;


	if (qt_fichas_w = qt_fichas_lote_w) then
		ds_retorno_w := 'N';
	else
		ds_retorno_w := 'S';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
