CREATE OR REPLACE FUNCTION verifica_historico_obrig ( nr_seq_ordem_p bigint, nr_seq_tipo_p bigint) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
-------------------------------------------------------------------------------------------------------------------
Referências:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
qt_registro_w		bigint;
ds_tipo_w		varchar(80)	:= null;


BEGIN
select 	count(1)
into STRICT	qt_registro_w
from	man_ordem_serv_tecnico
where	nr_seq_ordem_serv = nr_seq_ordem_p
and	nr_seq_tipo	= nr_seq_tipo_p;

if (qt_registro_w = 0) then
	select	max(substr(obter_desc_expressao(cd_exp_tipo, ds_tipo),1,80))
	into STRICT	ds_tipo_w
	from	man_tipo_hist
	where	nr_sequencia = nr_seq_tipo_p;
end if;

return ds_tipo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
