CREATE OR REPLACE FUNCTION verifica_horario_livre_agenda (cd_agenda_p bigint, dt_agenda_p timestamp, nr_seq_agenda_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(01)	:= 'S';
qt_horarios_marcados_w		bigint 	:= 0;


BEGIN

if (nr_seq_agenda_p <> 0) then
	begin

	select	count(*)
	into STRICT	qt_horarios_marcados_w
	from	agenda_consulta
	where	cd_agenda	= cd_agenda_p
	and	dt_agenda	= dt_agenda_p
	and	nr_sequencia	<> nr_seq_agenda_p
	and	not ie_status_agenda	 in ('L','C','F','I', 'LF');

	end;
else
	begin

	select	count(*)
	into STRICT	qt_horarios_marcados_w
	from	agenda_consulta
	where	cd_agenda	= cd_agenda_p
	and	dt_agenda	= dt_agenda_p
	and	not ie_status_agenda	 in ('L','C','F','I', 'LF');

	end;
end if;

if (qt_horarios_marcados_w > 0) then
	ds_retorno_w	:= 'N';
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
