CREATE OR REPLACE FUNCTION verifica_idade_min_max ( nr_idade_min_max_p text, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE

nr_min_w	bigint;
nr_max_w	bigint;
nr_idade_w	bigint:= 0;
nr_barra_w	bigint:= 0;
ds_retorno_w	varchar(255):= 'N';


BEGIN

begin

nr_idade_w := (obter_idade_pf(cd_pessoa_fisica_p, clock_timestamp(), 'A'))::numeric;

if (nr_idade_min_max_p IS NOT NULL AND nr_idade_min_max_p::text <> '') then
	nr_barra_w := position('/' in nr_idade_min_max_p);

	nr_min_w:=	(substr(nr_idade_min_max_p,1,nr_barra_w - 1))::numeric;
	nr_max_w:=	(substr(nr_idade_min_max_p,(nr_barra_w + 1),length(nr_idade_min_max_p)))::numeric;
end if;

if (nr_idade_w < nr_min_w) or (nr_idade_w > nr_max_w) then
	ds_retorno_w:= 'S';
else
	ds_retorno_w:= 'N';
end if;
exception when others then
	ds_retorno_w:= 'N';
end;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
