CREATE OR REPLACE FUNCTION verifica_material_similar (cd_material1_p bigint, cd_material2_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(01);
cd_material_gen1_p	integer;
cd_material_gen2_p	integer;


BEGIN

ie_retorno_w		:= 'N';

select	coalesce(max(cd_material_generico),0)
into STRICT	cd_material_gen1_p
from	material
where	cd_material	= cd_material1_p;

select	coalesce(max(cd_material_generico),0)
into STRICT	cd_material_gen2_p
from	material
where	cd_material	= cd_material2_p;


if (cd_material1_p = cd_material2_p) or (cd_material1_p = cd_material_gen2_p) or (cd_material2_p = cd_material_gen1_p) or (cd_material_gen1_p = cd_material_gen2_p) then
	ie_retorno_w		:= 'S';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
