SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION verifica_medicamentos_uso_data (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ie_existe_registo_w	varchar(1);
qt_medicamento_w	bigint;

BEGIN

ie_existe_registo_w := 'S';
qt_medicamento_w := 0;


if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	select	count(*)
	into STRICT	qt_medicamento_w
	from 	paciente_medic_uso
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	and	trunc(dt_registro) = trunc(clock_timestamp());

	if (qt_medicamento_w > 0) then
		ie_existe_registo_w := 'S';
	else
		ie_existe_registo_w := 'N';
	end if;
end if;

return	ie_existe_registo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
