CREATE OR REPLACE FUNCTION verifica_mesano_ref_conv ( cd_convenio_p bigint, dt_mesano_ref_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(01)	:= 'N';
dt_conta_w		timestamp;
dt_convenio_w		timestamp;
dt_convenio_mesant_w	timestamp;


BEGIN

select	trunc(dt_mesano_ref_p,'month')
into STRICT	dt_conta_w
;

select	trunc(dt_ref_valida,'month'),
	(trunc(dt_ref_valida,'month') - 30)
into STRICT	dt_convenio_w,
	dt_convenio_mesant_w
from 	convenio
where 	cd_convenio	= cd_convenio_p;

if (trunc(dt_conta_w, 'month')	= trunc(dt_convenio_w, 'month')) or (trunc(dt_conta_w, 'month')	= trunc(dt_convenio_mesant_w, 'month')) then
	ds_retorno_w	:= 'S';
end if;

return	ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
