CREATE OR REPLACE FUNCTION verifica_obrigatoriedade_nf ( cd_material_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


cd_grupo_material_w	smallint;
cd_subgrupo_material_w	smallint;
cd_classe_material_w	integer;
nr_sequencia_w		bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	regra_obriga_nf_estrut
	where	cd_estabelecimento = cd_estabelecimento_p
	and	coalesce(cd_grupo_material,coalesce(cd_grupo_material_w,0)) = coalesce(cd_grupo_material_w,0)
	and	coalesce(cd_subgrupo_material,coalesce(cd_subgrupo_material_w,0)) = coalesce(cd_subgrupo_material_w,0)
	and	coalesce(cd_classe_material,coalesce(cd_classe_material_w,0)) = coalesce(cd_classe_material_w,0)
	and	coalesce(cd_material,coalesce(cd_material_p,0)) = coalesce(cd_material_p,0)
	order by cd_material desc,
		cd_grupo_material desc,
		cd_subgrupo_material desc,
		cd_classe_material desc;


BEGIN

select	obter_estrutura_material(cd_material_p,'G'),
	obter_estrutura_material(cd_material_p,'S'),
	obter_estrutura_material(cd_material_p,'C')
into STRICT	cd_grupo_material_w,
	cd_subgrupo_material_w,
	cd_classe_material_w

order by obter_estrutura_material(cd_material_p,'G'),
	obter_estrutura_material(cd_material_p,'S'),
	obter_estrutura_material(cd_material_p,'C');

open C01;
loop
fetch C01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	nr_sequencia_w := nr_sequencia_w;

	end;
end loop;
close C01;

return	nr_sequencia_w;

end;
$body$
