SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION verifica_pac_atend_grupo ( cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ds_grupo_w	varchar(60);
nr_seq_grupo_w	bigint;
ds_mensagem_w	varchar(100);




BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	begin

		select	c.nr_seq_grupo
		into STRICT	nr_seq_grupo_w
		from	regra_agenda_grupo_atend c
		where	c.ie_situacao	= 'A'  LIMIT 1;
	exception
		when others then
		nr_seq_grupo_w	:= null;
	end;

	if (nr_seq_grupo_w IS NOT NULL AND nr_seq_grupo_w::text <> '') then
		select	max(b.ds_grupo)
		into STRICT	ds_grupo_w
		from	pac_grupo_atend a,
			pac_grupo b
		where	a.nr_seq_grupo	= b.nr_sequencia
		and	a.cd_pessoa_fisica	= cd_pessoa_fisica_p
		and	a.nr_seq_grupo in (
					SELECT	c.nr_seq_grupo
					from	regra_agenda_grupo_atend c
					where	c.ie_situacao	= 'A');
	end if;

	if (ds_grupo_w IS NOT NULL AND ds_grupo_w::text <> '') then
		ds_mensagem_w	:= substr(wheb_mensagem_pck.get_texto(796787,'DS_GRUPO_W='||ds_grupo_w),1,100);
	end if;

end if;

return	ds_mensagem_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
