CREATE OR REPLACE FUNCTION verifica_peso_altura_ciclo (nr_seq_paciente_p bigint, nr_atendimento_p bigint, nr_ciclo_p bigint default null) RETURNS varchar AS $body$
DECLARE


ds_retorno_w    varchar(1) := 'N';
qt_peso_origem_w    double precision;
qt_altura_origem_w  double precision;
qt_peso_onc_w  paciente_atendimento.qt_peso%type;
qt_altura_onc_w  double precision;
ie_origem_dados_peso_altura_w	varchar(10);

c01 CURSOR FOR
  SELECT  qt_peso,qt_altura
  from  paciente_atendimento
  where  coalesce(nr_prescricao::text, '') = ''
  and  nr_seq_paciente = nr_seq_paciente_p
  and  nr_ciclo = coalesce(nr_ciclo_p, nr_ciclo);


BEGIN
ie_origem_dados_peso_altura_w := Obter_param_Usuario(281, 586, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_origem_dados_peso_altura_w);

if (ie_origem_dados_peso_altura_w = 'SV') then
    select  obter_sinal_vital(nr_atendimento_p,'Peso')
    into STRICT  qt_peso_origem_w
;
    select obter_sinal_vital(nr_atendimento_p,'Altura')
    into STRICT qt_altura_origem_w
;
end if;

if (ie_origem_dados_peso_altura_w = 'PT') then
    select  qt_peso, qt_altura
    into STRICT    qt_peso_origem_w, qt_altura_origem_w
    from    paciente_setor
    where   nr_seq_paciente = nr_seq_paciente_p;
end if;


open C01;
loop
fetch C01 into
    qt_peso_onc_w,
    qt_altura_onc_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
    begin
    if ((qt_peso_origem_w <> qt_peso_onc_w)or (qt_altura_origem_w <> qt_altura_onc_w)) then
        ds_retorno_w := 'S';
        exit;
    end if;
    end;
end loop;
close C01;

return  ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
