CREATE OR REPLACE FUNCTION verifica_peso_ciclo ( nr_seq_paciente_p bigint, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w		varchar(1) := 'N';
qt_peso_sv_w		double precision;
qt_peso_onc_w		double precision;	
 
c01 CURSOR FOR 
	SELECT	qt_peso 
	from	paciente_atendimento 
	where	coalesce(nr_prescricao::text, '') = '' 
	and	nr_seq_paciente = nr_seq_paciente_p;


BEGIN 
select	obter_sinal_vital(nr_atendimento_p,'Peso') 
into STRICT	qt_peso_sv_w
;
 
open C01;
loop 
fetch C01 into	 
	qt_peso_onc_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	if (qt_peso_sv_w <> qt_peso_onc_w) then 
		ds_retorno_w := 'S';
		exit;
	end if;
	end;
end loop;
close C01;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
