CREATE OR REPLACE FUNCTION verifica_pre_agend_cid_pend ( cd_agenda_p bigint, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_retorno_w		varchar(5) := 'N';
cd_especialidade_w	bigint;
			

BEGIN 
 
if (cd_agenda_p IS NOT NULL AND cd_agenda_p::text <> '') and (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then 
 
	select	max(cd_especialidade) 
	into STRICT	cd_especialidade_w 
	from	agenda 
	where	cd_agenda	= cd_agenda_p;
	 
	 
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  
	into STRICT	ie_retorno_w 
	from	atend_cid_esp_pre_agend 
	where	cd_especialidade	= cd_especialidade_w 
	and	nr_atendimento		= nr_atendimento_p 
	and	coalesce(nr_seq_agenda_destino::text, '') = '';
 
end if;
 
return	ie_retorno_w;
 
end;
