CREATE OR REPLACE FUNCTION verifica_regra_turno_usuario (cd_agenda_p bigint, dt_agenda_p timestamp, cd_pessoa_fisica_p text, nr_seq_horario_p bigint) RETURNS varchar AS $body$
DECLARE

			
ie_retorno_w	varchar(1);
nr_seq_horario_w bigint;
qt_regras_usuario_w bigint;

BEGIN

  ie_retorno_w := 'S';

  if (coalesce(nr_seq_horario_p::text, '') = '') then
    nr_seq_horario_w := obter_turno_encaixe_ageexame(cd_agenda_p, dt_agenda_p);
  else
    nr_seq_horario_w := nr_seq_horario_p;
  end if;

  select count(1)
  into STRICT qt_regras_usuario_w
  from agenda_horario_permissao
  where nr_seq_horario = nr_seq_horario_w
  and ie_situacao = 'A';

  if (qt_regras_usuario_w > 0) then

    select CASE WHEN count(1)=0 THEN  'N'  ELSE 'S' END
    into STRICT ie_retorno_w
    from agenda_horario_permissao
    where nr_seq_horario = nr_seq_horario_w
    and cd_pessoa_fisica = cd_pessoa_fisica_p
    and ie_situacao = 'A';

  end if;

  return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
