SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION verifica_reserva_paciente (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE

			 
ie_existe_reserva_w	varchar(1);
qt_reserva_w	    bigint;			
ie_consiste_status_w	varchar(1);


BEGIN 
 
ie_existe_reserva_w := 'N';
ie_consiste_status_w := coalesce(Obter_Valor_Param_Usuario(44,190, Obter_perfil_Ativo, wheb_usuario_pck.get_nm_usuario, 0),'N');
 
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then 
	 
	select  count(*) 
	into STRICT	 qt_reserva_w 
	from   ocupacao_unidade_v                                
	where  cd_paciente_reserva = cd_pessoa_fisica_p    
	and   ((ie_consiste_status_w = 'N') or (ie_status_unidade = 'R'));
	 
	if (qt_reserva_w > 0) then 
		ie_existe_reserva_w := 'S';
	else 
		ie_existe_reserva_w := 'N';
	end if;
end if;
 
return	ie_existe_reserva_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
