SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION verifica_resposta_parecer_pend ( nm_usuario_p text, nr_parecer_p bigint default null) RETURNS varchar AS $body$
DECLARE


ds_retorno		varchar(1);
cd_pessoa_usuario_w 	varchar(10);


BEGIN

cd_pessoa_usuario_w := OBTER_PESSOA_FISICA_USUARIO(nm_usuario_p, 'C');	
				
select	coalesce(max('S'),'N')
into STRICT	ds_retorno 
from	PARECER_MEDICO_REQ a 
where ((nr_parecer_p IS NOT NULL AND nr_parecer_p::text <> '') and a.nr_parecer = nr_parecer_p )
or ( a.nr_parecer	not in (SELECT b.nr_parecer 
		    from   PARECER_MEDICO b 
		    where  b.nr_parecer = a.nr_parecer	
		    and    (b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '')
			and    coalesce(b.dt_inativacao::text, '') = '')
and	(((a.CD_PESSOA_PARECER IS NOT NULL AND a.CD_PESSOA_PARECER::text <> '') 
      and a.CD_PESSOA_PARECER = cd_pessoa_usuario_w)
	or ((a.nr_seq_equipe_dest IS NOT NULL AND a.nr_seq_equipe_dest::text <> '') 
        and a.nr_seq_equipe_dest in (select c.nr_seq_equipe 
							 from 	pf_equipe_partic c 
							 where	c.nr_seq_equipe = a.nr_seq_equipe_dest 
							 and	c.cd_pessoa_fisica = cd_pessoa_usuario_w))))
and (a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
and coalesce(a.dt_inativacao::text, '') = '';

return	ds_retorno;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
