CREATE OR REPLACE FUNCTION verifica_saldo_baixar (nr_prescricao_p bigint, nr_sequencia_p bigint, cd_estabelecimento_p bigint, cd_motivo_baixa_query_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1):= 'S';
varIe_Baixa_Compl_w	varchar(1);/*ParÃ¢metro 26 */
qt_total_dispensar_w	double precision;
qt_material_w		bigint;
ie_conta_paciente_w	varchar(1);


BEGIN

varIe_Baixa_Compl_w := obter_param_usuario(7025, 26, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, varIe_Baixa_Compl_w);

ie_retorno_w := 'S';
if (varIe_Baixa_Compl_w = 'S') then

	select 	a.qt_total_dispensar,
		sum(b.qt_material)
	into STRICT	qt_total_dispensar_w,
		qt_material_w
	from 	material_atend_paciente b,
		prescr_material a
        where 	a.nr_prescricao = nr_prescricao_p
	and 	a.nr_sequencia = nr_sequencia_p
	and 	a.nr_prescricao = b.nr_prescricao
	and 	a.nr_sequencia = b.nr_sequencia_prescricao
	group by a.qt_total_dispensar;

	select 	coalesce(max(ie_conta_paciente),'N')
	into STRICT	ie_conta_paciente_w
	from 	tipo_baixa_prescricao
	where 	cd_tipo_baixa = cd_motivo_baixa_query_p;

	if (qt_total_dispensar_w > qt_material_w) and (ie_conta_paciente_w = 'S') then
		ie_retorno_w := 'N';
	end if;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
