CREATE OR REPLACE FUNCTION verifica_saldo_tit (NR_TITULO_P bigint) RETURNS varchar AS $body$
DECLARE


vl_saldo_titulo_w     titulo_receber.vl_saldo_titulo%type;


BEGIN

    select	a.vl_saldo_titulo
    into STRICT    vl_saldo_titulo_w
    from	titulo_receber a
    where	a.nr_titulo = NR_TITULO_P;

    if (coalesce(vl_saldo_titulo_w,0) > 0) then
        return 'TRUE';
    else
        return 'FALSE';
    end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
