CREATE OR REPLACE FUNCTION verifica_setor_esclusivo ( cd_procedimento_p bigint, ie_origem_proced_p bigint, cd_setor_atendimento_p bigint, nr_seq_proc_interno_p bigint, nm_usuario_p text) RETURNS bigint AS $body$
DECLARE


qt_retorno_w		bigint := 1;
qt_proc_interno_w	bigint;
cd_setor_exclusivo_w	integer;
qt_proc_setor_atend_w	bigint;


BEGIN

Select 	count(*)
into STRICT	qt_proc_interno_w
from	proc_interno_setor
where	nr_seq_proc_interno = nr_seq_proc_interno_p;

select	count(*)
into STRICT	qt_proc_setor_atend_w
from 	procedimento_setor_atend a
where 	a.cd_procedimento  = cd_procedimento_p
and 	a.ie_origem_proced = ie_origem_proced_p;

if (nr_seq_proc_interno_p IS NOT NULL AND nr_seq_proc_interno_p::text <> '') and (qt_proc_interno_w > 0) then
	begin
	Select 	count(*)
	into STRICT	qt_retorno_w
	from   	proc_interno_setor
	where  	nr_seq_proc_interno = nr_seq_proc_interno_p
	and    	cd_setor_atendimento = cd_setor_atendimento_p
	and		coalesce(cd_perfil,obter_perfil_ativo) = obter_perfil_ativo;
	end;
else
	begin

	select	cd_setor_exclusivo
	into STRICT	cd_setor_exclusivo_w
	from 	procedimento
	where 	cd_procedimento   =  cd_procedimento_p
	and 	ie_origem_proced  =  ie_origem_proced_p;

	if (coalesce(cd_setor_exclusivo_w,0) <> 0) then
		begin
		select	count(*)
		into STRICT	qt_retorno_w
		from 	usuario_setor_v b,
			procedimento a
		where 	a.cd_setor_exclusivo = b.cd_setor_atendimento
		and 	b.nm_usuario         =  nm_usuario_p
		and 	a.cd_procedimento    =  cd_procedimento_p
		and 	a.ie_origem_proced   =  ie_origem_proced_p
		and 	a.cd_setor_exclusivo =  cd_setor_atendimento_p;
		end;
	elsif (coalesce(qt_proc_setor_atend_w,0) > 0) then
		begin
		select 	count(*)
		into STRICT	qt_retorno_w
		from 	usuario_setor_v b,
			procedimento_setor_atend a
		where 	a.cd_setor_atendimento = b.cd_setor_atendimento
		and 	b.nm_usuario         	= nm_usuario_p
		and 	a.cd_procedimento    	= cd_procedimento_p
		and 	a.ie_origem_proced   	= ie_origem_proced_p
		and 	a.cd_setor_atendimento 	= cd_setor_atendimento_p;
		end;
	end if;
	end;
end if;

return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
