SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION verifica_setor_lib_proc (ie_regra_p text, cd_setor_atendimento_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_liberado_w		varchar(01);
cd_classif_setor_w	varchar(02);
qt_setor_usuario_w      bigint;
ie_regra_w		varchar(5);
qt_setor_estab_w	bigint;


BEGIN

ie_regra_w      := 'SU';
--ie_regra_p - ie_regra_w,   passando fixo pois o cliente pediu apenas para os setores do usuário, caso seja necessário outras opções pode ser passado pelo parâmetro
select	max(cd_classif_setor)
into STRICT	cd_classif_setor_w
from	setor_atendimento
where	cd_setor_atendimento	= cd_setor_atendimento_p;

if (cd_classif_setor_w in ('1','2','3','4','5','8','10')) then


	select	count(*)
	into STRICT	qt_setor_estab_w
	from	setor_atendimento
	where	cd_setor_atendimento = cd_setor_atendimento_p
	and	cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento;

	if (qt_setor_estab_w > 0) then


		select	count(*)
		into STRICT	qt_setor_usuario_w
		from	usuario_setor
		where	cd_setor_atendimento	= cd_setor_atendimento_p
		and	nm_usuario_param	= nm_usuario_p;

		if (qt_setor_usuario_w > 0) then
			ie_liberado_w		:= 'S';
		else
			ie_liberado_w		:= 'N';
		end if;
	else
		ie_liberado_w		:= 'N';
	end if;
end if;
return	ie_liberado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
