SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION verifica_se_agrup_isol (cd_setor_atendimento_p bigint, nr_agrupamento_p bigint) RETURNS varchar AS $body$
DECLARE

 
qt_leito_isol_w	bigint;
qt_paciente_isol_w bigint;
ie_leito_isol_w	varchar(1);	
			 

BEGIN 
 
ie_leito_isol_w := 'N';
 
select	count(*) 
into STRICT	qt_paciente_isol_w	 
from 	atendimento_paciente a, 
	unidade_atendimento b 
where 	a.nr_atendimento = b.nr_atendimento 
and   b.cd_setor_atendimento = cd_setor_atendimento_p 
and   b.nr_agrupamento = nr_agrupamento_p 
and   a.ie_paciente_isolado = 'S';
 
 
select	count(*) 
into STRICT	qt_leito_isol_w 
from	unidade_atendimento 
where	cd_setor_atendimento = cd_setor_atendimento_p 
and	nr_agrupamento = nr_agrupamento_p 
and	ie_status_unidade = 'O';
 
 
if	((qt_paciente_isol_w > 0) or (qt_leito_isol_w > 0)) then 
	 
	ie_leito_isol_w := 'S';
	 
end if;
 
return	ie_leito_isol_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
