CREATE OR REPLACE FUNCTION verifica_se_atualiza_medico (cd_convenio_p bigint) RETURNS varchar AS $body$
DECLARE


ie_atualiza_w		varchar(1);
qt_regra_w			bigint;


BEGIN

if (cd_convenio_p IS NOT NULL AND cd_convenio_p::text <> '') then
	select  count(*)
	into STRICT	qt_regra_w
	from	lab_atual_convenio_medico
	where	cd_convenio = cd_convenio_p
	and		ie_atualizar_medic_exec = 'S';

	if (qt_regra_w > 0)	 then
		ie_atualiza_w := 'S';
	else
		ie_atualiza_w := 'N';
	end if;
end if;

return	ie_atualiza_w;

end;
