CREATE OR REPLACE FUNCTION verifica_se_consiste_inspecao ( nr_sequencia_p bigint, nr_seq_registro_p bigint, ie_tipo_liberar_p bigint, ie_forma_registro_p text) RETURNS varchar AS $body$
DECLARE


/* ie_tipo_liberar_p
1 - Liberar somente o item selecionado
2 - Liberar registro ou Liberar inspeção
3 - Liberar inspeção da ordem

   ie_forma_registro_p
S - Parâmetro [4] habilitado
N - Parâmetro [4] desabilitado
*/
ie_retorno_w	varchar(1) := 'N';
qt_existe_w	bigint;
nr_sequencia_w	bigint;

c01 CURSOR FOR
SELECT	nr_sequencia
from	inspecao_recebimento
where	nr_sequencia		= nr_sequencia_p
and	((ie_tipo_liberar_p		= 1 AND ie_forma_registro_p = 'S') or
	 (ie_tipo_liberar_p		= 2 AND ie_forma_registro_p = 'N'))

union all

select	nr_sequencia
from	inspecao_recebimento
where	nr_seq_registro		= nr_seq_registro_p
and	ie_tipo_liberar_p   		= 2
and	ie_forma_registro_p 		= 'S'

union all

select	nr_sequencia
from	inspecao_recebimento
where	nr_ordem_compra = (
	SELECT	nr_ordem_compra
	from	inspecao_recebimento
	where	nr_sequencia 	= nr_sequencia_p)
and	ie_tipo_liberar_p   		= 3
and	ie_forma_registro_p 		= 'N';


BEGIN

open C01;
loop
fetch C01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	count(*)
	into STRICT	qt_existe_w
	from	recebimento_insp_consist
	where   nr_seq_inspecao = nr_sequencia_w;

	if (qt_existe_w <> 0) and (ie_retorno_w = 'N') then
		ie_retorno_w := 'S';
	end if;

	end;
end loop;
close C01;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
