CREATE OR REPLACE FUNCTION verifica_se_existe_col_w_fluxo (nm_coluna_p text) RETURNS varchar AS $body$
DECLARE


ie_existe_w	varchar(1) := 'N';
ds_sql_dinamico	varchar(500);
qt_registros_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_registros_w
from	user_tab_columns
where	upper(table_name) = 'W_FLUXO_CAIXA_PERIODO'
and	upper(column_name) = upper(nm_coluna_p);

if (qt_registros_w > 0) then
	ds_sql_dinamico := 'select	count(*) '
			|| 'from	w_fluxo_caixa_periodo '
			|| 'where ' || nm_coluna_p || ' is not null';
	EXECUTE ds_sql_dinamico
	into STRICT qt_registros_w;
	if (qt_registros_w > 0) then
		ie_existe_w := 'S';
	end if;
end if;

return	ie_existe_w;

end;
$body$
LANGUAGE PLPGSQL
