CREATE OR REPLACE FUNCTION verifica_se_paciente_leito (cd_setor_atendimento_p bigint, nr_agrupamento_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(255);
qt_paciente_leito_w	bigint;

BEGIN

ds_retorno_w := '';

select	count(*)
into STRICT	qt_paciente_leito_w
from	unidade_atendimento
where	cd_setor_atendimento = cd_setor_atendimento_p
and	nr_agrupamento = nr_agrupamento_p
and	(nr_atendimento IS NOT NULL AND nr_atendimento::text <> '');

if (qt_paciente_leito_w >= 1) then

	ds_retorno_w := wheb_mensagem_pck.get_texto(417923);
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
