SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION verifica_se_permite_suspender (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_status_atend_param_w		varchar(255);
qt_w				bigint;
ds_retorno_w			varchar(1) := 'N';


BEGIN


ie_status_atend_param_w := obter_param_usuario(916, 984, Obter_perfil_Ativo, obter_usuario_ativo, obter_estabelecimento_ativo, ie_status_atend_param_w);


select 	count(*)
into STRICT	qt_w
from	prescr_medica a,
	prescr_procedimento b
where	a.nr_atendimento = nr_atendimento_p
and	a.nr_prescricao  = b.nr_prescricao
AND	((b.nr_seq_exame IS NOT NULL AND b.nr_seq_exame::text <> '') and (position(',' || b.ie_status_atend || ',' in ',' || ie_status_atend_param_w  || ',' ) > 0));



if (qt_w > 0 ) then
	ds_retorno_w := 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
