CREATE OR REPLACE FUNCTION verifica_se_primeira_escolha ( nr_seq_resultado_p bigint, nr_seq_result_item_p bigint, cd_microorganismo_p bigint, nr_cultura_microorg_p text default null) RETURNS varchar AS $body$
DECLARE


