CREATE OR REPLACE FUNCTION verifica_se_proc_conv ( cd_estabelecimento_p bigint, cd_convenio_p bigint, cd_categoria_p text, dt_conta_p timestamp, cd_procedimento_p bigint, ie_origem_proced_p bigint, nr_seq_proc_interno_p bigint, ie_tipo_atendimento_p atendimento_paciente.ie_tipo_atendimento%type default null) RETURNS varchar AS $body$
DECLARE

ds_retorno_w		varchar(2);
ie_classificacao_w	smallint;
cd_procedimento_w	bigint;
ie_origem_proced_w	bigint;


BEGIN

cd_procedimento_w		:= cd_procedimento_p;
ie_origem_proced_w	:= ie_origem_proced_p;

if (coalesce(nr_seq_proc_interno_p,0) > 0) then

	SELECT * FROM Obter_Proc_Tab_Interno_Conv2(	nr_seq_proc_interno_p, cd_estabelecimento_p, cd_convenio_p, cd_categoria_p, null, dt_conta_p, null, cd_procedimento_w, ie_origem_proced_w, ie_tipo_atendimento_p) INTO STRICT cd_procedimento_w, ie_origem_proced_w;
end if;


select	coalesce(max(ie_classificacao),1)
into STRICT	ie_classificacao_w
from	procedimento
where	cd_procedimento		= cd_procedimento_w
and	ie_origem_proced	= ie_origem_proced_w;



if (ie_classificacao_w	= 1) then

	select	Verifica_se_proc_edicao(cd_estabelecimento_p,cd_convenio_p,cd_categoria_p,dt_conta_p,cd_procedimento_w)
	into STRICT	ds_retorno_w
	;

else
	select	Verifica_Se_proc_Tab_Serv(cd_estabelecimento_p,cd_convenio_p,cd_categoria_p,dt_conta_p,cd_procedimento_w)
	into STRICT	ds_retorno_w
	;
end if;
	
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
