CREATE OR REPLACE FUNCTION verifica_se_proc_regra_interv ( nr_prescricao_p bigint, nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ie_gerar_intervalo_w		varchar(1) := 'N';
cd_grupo_proc_w			bigint;
cd_especialidade_w		bigint;
cd_area_procedimento_w		bigint;
ie_origem_proced_w		bigint;
cd_procedimento_w		bigint;
nr_seq_exame_w			bigint;
cd_intervalo_w			varchar(50);
nr_seq_proc_interno_w		bigint;
cd_setor_atendimento_w		integer;

C01 CURSOR FOR
SELECT	ie_gerar_intervalo
from	rep_regra_proced_intervalo
where	coalesce(cd_grupo_proc, cd_grupo_proc_w) = cd_grupo_proc_w
and	coalesce(cd_especialidade, cd_especialidade_w) = cd_especialidade_w
and	coalesce(cd_area_procedimento,cd_area_procedimento_w) = cd_area_procedimento_w
and	coalesce(cd_procedimento,cd_procedimento_w) = cd_procedimento_w
and	coalesce(nr_seq_exame, coalesce(nr_seq_exame_w,0)) = coalesce(nr_seq_exame_w,0)
and	coalesce(nr_seq_proc_interno,coalesce(nr_seq_proc_interno_w,0)) = coalesce(nr_seq_proc_interno_w,0)
and	((coalesce(cd_intervalo_excl::text, '') = '') or (cd_intervalo_excl <> cd_intervalo_w))
and	((coalesce(cd_procedimento::text, '') = '') or (coalesce(ie_origem_proced,ie_origem_proced_w) = ie_origem_proced_w))
and	((coalesce(cd_setor_atendimento::text, '') = '') or (coalesce(cd_setor_atendimento,cd_setor_atendimento_w) = cd_setor_atendimento_w))
order by coalesce(cd_procedimento,999999999999999) desc,
	coalesce(nr_seq_exame,9999) desc,
	coalesce(cd_area_procedimento, 99999999) desc,
	coalesce(cd_especialidade, 99999999) desc,
	coalesce(cd_grupo_proc,999999999999999) desc,
	coalesce(nr_seq_proc_interno,9999999999) desc,
	coalesce(cd_setor_atendimento,9999999999) desc;


BEGIN


select	max(cd_grupo_proc),
	max(cd_especialidade),
	max(cd_area_procedimento),
	max(b.cd_procedimento),
	max(b.ie_origem_proced),
	max(b.cd_intervalo),
	max(b.nr_seq_proc_interno),
	max(b.nr_seq_exame),
	max(b.cd_setor_atendimento)
into STRICT	cd_grupo_proc_w,
	cd_especialidade_w,
	cd_area_procedimento_w,
	cd_procedimento_w,
	ie_origem_proced_w,
	cd_intervalo_w,
	nr_seq_proc_interno_w,
	nr_seq_exame_w,
	cd_setor_atendimento_w
from	prescr_procedimento b,
	estrutura_procedimento_v a
where	a.cd_procedimento	= b.cd_procedimento
and	a.ie_origem_proced	= b.ie_origem_proced
and	b.nr_prescricao		= nr_prescricao_p
and	b.nr_sequencia		= nr_sequencia_p;


open C01;
loop
fetch C01 into
	ie_gerar_intervalo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	ie_gerar_intervalo_w	:= ie_gerar_intervalo_w;
end loop;
close C01;

return	ie_gerar_intervalo_w;

end;
$body$
