CREATE OR REPLACE FUNCTION verifica_se_proc_tab_serv (cd_estabelecimento_p bigint, cd_convenio_p bigint, cd_categoria_p text, dt_conta_p timestamp, cd_procedimento_p bigint) RETURNS varchar AS $body$
DECLARE


dt_vigencia_w			timestamp;
dt_final_vigencia_w		timestamp;
cd_tabela_servico_w		integer;
ie_origem_proced_w		bigint;
ie_origem_proced_edicao_w	bigint;
qt_preco_w			bigint	:= 0;
TX_AJUSTE_GERAL_w		double precision;
ie_liberado_edicao_w		varchar(01)	:= 'N';
ie_tipo_convenio_w		smallint;
nr_aux_w			double precision;
ds_aux_w			varchar(200);
ie_glosa_w			varchar(2);
vl_servico_w			double precision;

c01 CURSOR FOR
	SELECT	b.cd_tabela_servico
	from	convenio_servico b
	where	b.cd_convenio		= cd_convenio_p
	and	b.cd_estabelecimento 	= cd_estabelecimento_p
	and (b.cd_categoria		= cd_categoria_p or coalesce(cd_categoria_p::text, '') = '')
	--and	b.dt_liberacao_tabela	<= dt_conta_p
	and	establishment_timezone_utils.startofday(dt_conta_p)	between b.dt_liberacao_tabela and coalesce(b.dt_termino, dt_conta_p)
	and 	coalesce(b.ie_situacao,'A')	= 'A'
	order by coalesce(b.nr_prioridade,1) desc;


BEGIN

select	max(ie_tipo_convenio)
into STRICT	ie_tipo_convenio_w
from	convenio
where	cd_convenio	= cd_convenio_p;

if (ie_tipo_convenio_w	= 3) then
	ie_liberado_edicao_w	:= 'S';
else
	open	c01;
	loop
	fetch	c01 into
		cd_tabela_servico_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin	

		if (qt_preco_w = 0) then
			begin

			select	count(*)
			into STRICT	qt_preco_w
			from	preco_servico
			where	CD_TABELA_SERVICO	= cd_tabela_servico_w
			and	cd_estabelecimento	= cd_estabelecimento_p
			and	cd_procedimento		= cd_procedimento_p
                        and (establishment_timezone_utils.startofday(dt_conta_p) between dt_inicio_vigencia and coalesce(dt_vigencia_final,establishment_timezone_utils.startofday(dt_conta_p)))
                        and (coalesce(dt_inativacao::text, '') = '');

			if (qt_preco_w > 0) then --sevico dentro da vigencia
				ie_liberado_edicao_w	:= 'S';
			end if;

			end;
		end if;

		end;
	end loop;
	close c01;
end if;

return	ie_liberado_edicao_w;
--dentro da dt liberacao e dt 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION verifica_se_proc_tab_serv (cd_estabelecimento_p bigint, cd_convenio_p bigint, cd_categoria_p text, dt_conta_p timestamp, cd_procedimento_p bigint) FROM PUBLIC;

