CREATE OR REPLACE FUNCTION verifica_se_sup_reconhecido (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_superior_w		bigint;
dt_acordo_externo_w		timestamp;
ie_reconhecido_w		varchar(1);
nr_nivel_w			bigint;
nr_nivel_sup_w			bigint;


BEGIN
	ie_reconhecido_w := 'N';

	select	a.nr_seq_superior,
		a.dt_acordo_externo,
		proj_obter_nivel_etapa(a.nr_sequencia)
	into STRICT	nr_seq_superior_w,
		dt_acordo_externo_w,
		nr_nivel_w
	from	proj_cron_etapa a
	where	a.nr_sequencia = nr_sequencia_p;

	if (dt_acordo_externo_w IS NOT NULL AND dt_acordo_externo_w::text <> '') then
		ie_reconhecido_w := 'S';
	else
		select	proj_obter_nivel_etapa(b.nr_sequencia)
		into STRICT	nr_nivel_sup_w
		from	proj_cron_etapa b
		where	b.nr_sequencia = nr_seq_superior_w;

		if (nr_seq_superior_w IS NOT NULL AND nr_seq_superior_w::text <> '') and (nr_nivel_sup_w < nr_nivel_w) then
			ie_reconhecido_w := verifica_se_sup_reconhecido(nr_seq_superior_w);
		end if;
	end if;

return	ie_reconhecido_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
