SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION verifica_tempo_internacao_ps (nr_atendimento_p bigint, dt_alta_p timestamp) RETURNS varchar AS $body$
DECLARE

			 
ie_consiste_ps_w	varchar(1);
ie_tipo_atendimento_w	smallint;
dt_entrada_w		timestamp;
qt_hora_w		bigint;
dt_alta_w		timestamp;			

BEGIN 
 
dt_alta_w	:= coalesce(dt_alta_p,clock_timestamp());
 
ie_consiste_ps_w := 'N';
 
if (coalesce(nr_atendimento_p,0) > 0) then 
 
	select	max(ie_tipo_atendimento) 
	into STRICT	ie_tipo_atendimento_w 
	from	atendimento_Paciente 
	where	nr_atendimento	= nr_atendimento_p;
	 
	if (ie_tipo_atendimento_w = 3) then 
	 
		select	max(dt_entrada) 
		into STRICT	dt_entrada_w 
		from	atendimento_Paciente 
		where	nr_atendimento	= nr_atendimento_p;
		 
		qt_hora_w := obter_min_entre_datas(dt_entrada_w, dt_alta_w,'');
		 
		if (qt_hora_w >= 1440) then 
			ie_consiste_ps_w := 'S';
			 
		else 
			ie_consiste_ps_w := 'N';
		end if;
	else 
	 
		ie_consiste_ps_w := 'N';
	end if;
end if;
 
return	ie_consiste_ps_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
