CREATE OR REPLACE FUNCTION verifica_turno_producao ( nr_sequencia_p NUT_PRODUCAO_LACTARIO.NR_SEQUENCIA%type, dt_producao_p NUT_PRODUCAO_LACTARIO.DT_PRODUCAO%type, nr_seq_turno_p NUT_PRODUCAO_LACTARIO.NR_SEQ_TURNO%type ) RETURNS bigint AS $body$
DECLARE


turno_w double precision := 0;


BEGIN

	SELECT coalesce(COUNT(NR_SEQUENCIA), 0)
	INTO STRICT turno_w 
	FROM NUT_PRODUCAO_LACTARIO  
	WHERE NR_SEQUENCIA <> nr_sequencia_p
	AND TRUNC(DT_PRODUCAO) = TRUNC(dt_producao_p)
	AND NR_SEQ_TURNO = nr_seq_turno_p
	AND IE_TIPO = 'N';

	RETURN turno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
