SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION verify_closed_copayment ( nr_interno_conta_p conta_pac_deducao_conv.nr_seq_conta_orig%type ) RETURNS varchar AS $body$
DECLARE

    ds_return_w     varchar(1) := 'N';

BEGIN

if (pkg_i18n.get_user_locale = 'es_AR') then
    select  CASE WHEN count(a.nr_sequencia)=0 THEN  'N'  ELSE 'S' END
    into STRICT    ds_return_w
    from    conta_pac_deducao_conv a,
            conta_paciente b
    where   a.nr_seq_conta_des = b.nr_interno_conta
    and     b.ie_status_acerto = 2
    and     a.nr_seq_conta_orig = nr_interno_conta_p
    and     a.ie_tipo_calculo = 'C';
end if;

return ds_return_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
