CREATE OR REPLACE FUNCTION verify_exists_dar_table (nm_tabela_p text) RETURNS varchar AS $body$
DECLARE

  related_data_w bigint;
  exists_w varchar(1);

BEGIN
	SELECT
		COUNT(1)
	into STRICT
		related_data_w
	FROM
		tabela_sistema ts
	WHERE
		upper(ts.nm_tabela) = upper(nm_tabela_p);

	IF (related_data_w > 0) THEN
		exists_w := 'S';
	ELSE
		exists_w := 'N';
	END IF;	

 RETURN exists_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
