CREATE OR REPLACE FUNCTION verify_exists_datamodel_dar (nr_seq_filter_p bigint) RETURNS varchar AS $body$
DECLARE

  related_data_w bigint;
  exists_w varchar(1);

BEGIN
	SELECT
		COUNT(1)
	into STRICT
		related_data_w
	FROM
		dar_app_datamodels dad,
		dar_tables_control dtc
	WHERE
		dtc.nr_seq_sql = nr_seq_filter_p
		AND dad.nr_seq_table_control = dtc.nr_sequencia;
	
	IF (related_data_w > 0) THEN
		exists_w := 'S';
	ELSE
		exists_w := 'N';
	END IF;	

 RETURN exists_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
