CREATE OR REPLACE FUNCTION verify_exists_data_or_display (nr_seq_filter_p bigint) RETURNS varchar AS $body$
DECLARE

  related_data_w bigint;
  related_display_w bigint;
  exists_w varchar(1);

BEGIN
	SELECT
		COUNT(1)
	into STRICT
		related_data_w
	FROM
		query_builder_dados
	WHERE
		nr_seq_filter = nr_seq_filter_p;

	SELECT
		COUNT(1)
	into STRICT
		related_display_w
	FROM
		query_builder_display
	WHERE
		nr_seq_filter = nr_seq_filter_p;

	IF (related_data_w > 0 OR related_display_w > 0) THEN
		exists_w := 'S';
	ELSE
		exists_w := 'N';
	END IF;	

 RETURN exists_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
