SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION verify_exists_qb_date_filter (nr_seq_filter_p bigint) RETURNS varchar AS $body$
DECLARE

  filter_data_w bigint;
  exists_w      varchar(1);

BEGIN
    SELECT Count(1) 
    INTO STRICT   filter_data_w 
    FROM   dar_query_filter_date dqfd 
    WHERE  dqfd.nr_seq_filter = nr_seq_filter_p;

    IF ( filter_data_w > 0 ) THEN 
      exists_w := 'S';
    ELSE 
      exists_w := 'N';
    END IF;

    RETURN exists_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
