CREATE OR REPLACE FUNCTION verify_infusion_pump ( nr_seq_agente_p bomba_infusao.nr_seq_agente%type, nr_seq_cirurgia_p equipamento_cirurgia.nr_sequencia%type ) RETURNS varchar AS $body$
DECLARE


ds_bomba_vinculada_w 		w_flowsheet_cirurgia_info.ie_bomba_infusao%type;
	

BEGIN
	ds_bomba_vinculada_w := 'N';
	
	select case when count(bi.nr_sequencia) > 0 then 'S' else 'N' END

    into STRICT ds_bomba_vinculada_w
	from	bomba_infusao          bi,
		equipamento_cirurgia   ec
	where
		bi.nr_seq_equip_cirurgia = ec.nr_sequencia
		and bi.nr_seq_agente = nr_seq_agente_p
		and ec.nr_cirurgia = nr_seq_cirurgia_p;
	
return ds_bomba_vinculada_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
