CREATE OR REPLACE FUNCTION verify_user_interest_build ( nr_seq_sche_build_p bigint, nm_user_p text) RETURNS varchar AS $body$
DECLARE

  user_is_interested_w varchar(350) := '';
  counter_w            bigint        := 0;

BEGIN
  SELECT COUNT(*)
  INTO STRICT counter_w
  FROM build_user_interest
  WHERE nr_seq_sche_build = nr_seq_sche_build_p
  AND nm_user_build       = nm_user_p;
  IF Counter_W            > 0 THEN
    user_is_interested_w := 'Y';
  ELSE
    user_is_interested_w := 'N';
  END IF;
RETURN user_is_interested_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
