CREATE OR REPLACE FUNCTION verif_elem_npt_ped ( nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1);


BEGIN
select	coalesce(max('S'),'N')
into STRICT	ie_retorno_w
from	nut_pac a,
		nut_pac_elemento b where		a.nr_sequencia = b.nr_seq_nut_pac
and		coalesce(a.ie_npt_adulta,'S') = 'P'
and		a.nr_prescricao = nr_prescricao_p
and		b.qt_volume_final < 0 LIMIT 1;

return coalesce(ie_retorno_w,'N');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
