SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION verif_vencto_medic_nao_padrao ( cd_pessoa_fisica_p text, qt_dias_aviso_previo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_material_w			varchar(255);
ds_titulo_lista_w		varchar(255);
ds_lista_w				varchar(2000);
ds_retorno_w			varchar(2255);
dt_inicio_validade_w	timestamp;
dt_fim_validade_w		timestamp;

C01 CURSOR FOR
SELECT	substr(obter_desc_material(cd_material),1,50),
		dt_inicio_validade,
		(dt_inicio_validade+qt_dias_tratamento)
from	lib_material_paciente
where	cd_pessoa_fisica = cd_pessoa_fisica_p
and		(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
and		coalesce(dt_suspenso::text, '') = ''
and		clock_timestamp() >= ((dt_inicio_validade+qt_dias_tratamento) - qt_dias_aviso_previo_p);
										

BEGIN

ds_titulo_lista_w	:=	obter_desc_expressao(781851);--'O vencimento do período de liberação dos medicamentos abaixo está chegando ou expirou!' || chr(13) || chr(13);
open C01;
loop
fetch C01 into	
	ds_material_w,
	dt_inicio_validade_w,
	dt_fim_validade_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	
	 ds_lista_w := ds_lista_w || ds_material_w || chr(10) || chr(13) || wheb_mensagem_pck.get_texto(1066852,'DT_VALIDADE_W='|| to_char(dt_inicio_validade_w,'dd/mm/yyyy hh24:mi')) || ' ' || obter_desc_expressao(345087) || ' ' || to_char(dt_fim_validade_w,'dd/mm/yyyy hh24:mi') || chr(10) || chr(13);
	
end loop;
close C01;

if (ds_lista_w IS NOT NULL AND ds_lista_w::text <> '') then
	ds_retorno_w := substr(ds_titulo_lista_w || ' ' || ds_lista_w,1,2255);	
else
	ds_retorno_w := null;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION verif_vencto_medic_nao_padrao ( cd_pessoa_fisica_p text, qt_dias_aviso_previo_p bigint) FROM PUBLIC;

