CREATE OR REPLACE FUNCTION via_obter_aprovador_rel_desp ( cd_viagem_p text) RETURNS varchar AS $body$
DECLARE

 
 
 
ds_retorno_w	varchar(255);

 
 

BEGIN 
 
SELECT	SUBSTR(obter_nome_pf(coalesce(MAX(cd_pessoa_aprov_rel_desp), coalesce(MAX(cd_pessoa_aprov_rel_desp_sub),0))),1,60) 
 
INTO STRICT	ds_retorno_w 
 
FROM	fin_gv_pend_aprov 
 
WHERE	nr_seq_viagem = cd_viagem_p;
 
 
 
RETURN	ds_retorno_w;
 
 
 
END;
$body$
