CREATE OR REPLACE FUNCTION via_obter_aprovador ( cd_viagem_p text) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w	varchar(255);


BEGIN 
SELECT	SUBSTR(obter_nome_pf(coalesce(max(cd_pessoa_aprov), coalesce(MAX(cd_aprov_sub),0))),1,60) 
into STRICT	ds_retorno_w 
FROM	fin_gv_pend_aprov 
WHERE	nr_seq_viagem = cd_viagem_p;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
