CREATE OR REPLACE FUNCTION via_obter_desc_aeroporto ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

ds_retorno_w		varchar(255);
ds_aeroporto_w		varchar(255);
ds_sigla_w		varchar(255);


BEGIN
select	max(a.ds_aeroporto),
	max(a.ds_sigla)
into STRICT	ds_aeroporto_w,
	ds_sigla_w
from	via_aeroporto a
where	a.nr_sequencia = nr_sequencia_p;

if (coalesce(ie_opcao_p,'X') = 'N') then
	ds_retorno_w := ds_aeroporto_w;
elsif (coalesce(ie_opcao_p,'X') = 'S') then
	ds_retorno_w := ds_sigla_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
