SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION via_obter_valor ( nr_seq_viagem_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/* ie_opcao_p
T N- Transporte nosso custo
H N- Hospedagem  nosso custo*/
ds_retorno_w	varchar(255) := '';
vl_reserva_w	double precision;


BEGIN
if (coalesce(ie_opcao_p,'X') = 'TN') then
	begin
	select	sum(coalesce(a.vl_reserva,0))
	into STRICT	vl_reserva_w
	from	via_reserva a
	where	a.ie_responsavel_custo = 'N'
	and	(a.nr_seq_meio_transp IS NOT NULL AND a.nr_seq_meio_transp::text <> '')
	and	a.nr_seq_viagem = nr_seq_viagem_p;
	ds_retorno_w := to_char(vl_reserva_w);
	end;
elsif (coalesce(ie_opcao_p,'X') = 'HN') then
	begin
	select	sum(coalesce(obter_valor_total_hotel(a.nr_sequencia),0))
	into STRICT	vl_reserva_w
	from	via_reserva a
	where	a.ie_responsavel_custo = 'N'
	and	(a.nr_seq_hotel IS NOT NULL AND a.nr_seq_hotel::text <> '')
	and	a.nr_seq_viagem = nr_seq_viagem_p;
	ds_retorno_w := to_char(vl_reserva_w);
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
