CREATE OR REPLACE FUNCTION via_obter_vl_total_desp ( nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE


nr_retorno_w		double precision := 0;
vl_desp_total_w		double precision;
nr_sequencia_w		bigint;

c01 CURSOR FOR
SELECT	a.nr_sequencia
from	via_relat_desp a
where	a.nr_seq_viagem = nr_sequencia_p;


BEGIN
open C01;
loop
fetch C01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	coalesce(max(obter_valor_desp_viagem(nr_sequencia_w,'0')),0)
	into STRICT	vl_desp_total_w
	;
	nr_retorno_w := nr_retorno_w + vl_desp_total_w;
	end;
end loop;
close C01;

return	nr_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
