SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION vipe_obter_compostos ( nr_seq_horario_p bigint) RETURNS varchar AS $body$
DECLARE


ie_medic_composto_w	varchar(1);
ds_material_w		varchar(255);
ds_retorno_w		varchar(2000);
nr_prescricao_w		bigint;
nr_seq_material_w	bigint;
nr_agrupamento_w	bigint;

c01 CURSOR FOR
SELECT	substr(obter_desc_material(cd_material),1,80) || ' - ' || qt_dose || ' ' || cd_unidade_medida_dose
from	prescr_material
where	nr_prescricao	= nr_prescricao_w
and	nr_agrupamento	= nr_agrupamento_w
and	ie_agrupador	= 1
and	coalesce(cd_kit_material::text, '') = '';


BEGIN
if (nr_seq_horario_p IS NOT NULL AND nr_seq_horario_p::text <> '') then

	select	max(nr_prescricao),
		max(nr_seq_material)
	into STRICT	nr_prescricao_w,
		nr_seq_material_w
	from	prescr_mat_hor
	where	nr_sequencia	= nr_seq_horario_p;

	select	max(nr_agrupamento)
	into STRICT	nr_agrupamento_w
	from	prescr_material
	where	nr_prescricao	= nr_prescricao_w
	and	nr_sequencia	= nr_seq_material_w;

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_medic_composto_w
	from	prescr_material
	where	nr_prescricao	= nr_prescricao_w
	and	nr_sequencia	<> nr_seq_material_w
	and	nr_agrupamento	= nr_agrupamento_w
	and	ie_agrupador	= 1
	and	coalesce(cd_kit_material::text, '') = '';

	if (ie_medic_composto_w = 'S') then
		open C01;
		loop
		fetch C01 into
			ds_material_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			ds_retorno_w	:=  ds_material_w || chr(13) || chr(10) || ds_retorno_w;
		end loop;
		close C01;
	end if;

end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION vipe_obter_compostos ( nr_seq_horario_p bigint) FROM PUBLIC;

