CREATE OR REPLACE FUNCTION vipe_obter_dose_composto (nr_prescricao_p bigint, nr_seq_material_p bigint, nr_agrupamento_p bigint) RETURNS bigint AS $body$
DECLARE


qt_dose_w	prescr_material.qt_dose%type;
nr_seq_ficha_tecnica_w	bigint;
cont_w			bigint;
cd_um_dose_w	prescr_material.cd_unidade_medida_dose%type;
			

BEGIN

	select	max(a.nr_seq_ficha_tecnica),
			max(cd_unidade_medida_dose)
	into STRICT	nr_seq_ficha_tecnica_w,
			cd_um_dose_w
	from	material a,
		prescr_material b
	where	a.cd_material	= b.cd_material
	and	b.nr_prescricao	= nr_prescricao_p
	and	b.nr_sequencia	= nr_seq_material_p;

	select	count(*)
	into STRICT	cont_w
	from	material b,
		prescr_material a
	where	a.cd_material		= b.cd_material
	and	a.nr_prescricao		= nr_prescricao_p
	and	a.nr_sequencia		<> nr_seq_material_p
	and	a.nr_agrupamento	= nr_agrupamento_p
	and	b.nr_seq_ficha_tecnica	= nr_seq_ficha_tecnica_w
	and	a.ie_agrupador		= 1
	and	coalesce(a.nr_seq_substituto::text, '') = ''
	and	coalesce(a.cd_kit_material::text, '') = '';
	
	if (cont_w > 0) then
	
		select	sum(a.qt_dose)
		into STRICT	qt_dose_w
		from	material b,
				prescr_material a
		where	a.cd_material			= b.cd_material
		and	a.nr_prescricao				= nr_prescricao_p
		and	a.nr_agrupamento			= nr_agrupamento_p
		and	b.nr_seq_ficha_tecnica		= nr_seq_ficha_tecnica_w
		and	a.ie_agrupador				= 1
		and	coalesce(a.nr_seq_substituto::text, '') = ''
		and	coalesce(a.cd_kit_material::text, '') = ''
		AND a.cd_unidade_medida_dose	 = cd_um_dose_w;

	end if;
	
	if (coalesce(qt_dose_w::text, '') = '') then
		select	max(a.qt_dose)
		into STRICT	qt_dose_w
		from	prescr_material a
		where	a.nr_prescricao		= nr_prescricao_p
		and	a.nr_sequencia		= nr_seq_material_p;
	end if;

return	qt_dose_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION vipe_obter_dose_composto (nr_prescricao_p bigint, nr_seq_material_p bigint, nr_agrupamento_p bigint) FROM PUBLIC;

